import { useState } from 'react';

export default function HeroBanner({ 
  title = "Welcome to our website", 
  description = "Learn more about our services and how we can help you achieve your goals.", 
  buttonText = "Get Started", 
  buttonLink = "/contact" 
}) {
  return (
    <div className="bg-red-600 text-white py-16 px-4 md:px-8 lg:px-16 flex flex-col items-center text-center">
      <h1 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4">{title}</h1>
      <p className="text-lg md:text-xl mb-8 max-w-2xl">{description}</p>
      <a 
        href={buttonLink} 
        className="bg-white text-red-600 hover:bg-red-100 px-6 py-3 rounded-lg font-medium transition-colors duration-200"
      >
        {buttonText}
      </a>
    </div>
  );
}
